#include <stdio.h>
#include <stdlib.h>
#include <math.h>
// https://leetcode.cn/problems/valid-parentheses/

char pairs(char a)
{
    if (a == '}')
        return '{';
    if (a == ']')
        return '[';
    if (a == ')')
        return '(';
    return 0;
}

int isValid(char *s)
{

    int _len = strlen(s);
    int stactSize = _len / 2 + 1;
    char stc[stactSize];
    int point = 0;

    for (size_t i = 0; i < _len; i++)
    {
        printf("result %d \n", point);
        char left = pairs(s[i]);

        if (left == 0)
        {
            if (point >= stactSize)
            {
                return 0;
            }
            stc[point] = s[i];
            point++;
            continue;
        }
        if (point == 0)
        {
            return 0;
        }
        point--;
        char stackTop = stc[point];
        if (stackTop != left)
        {
            return 0;
        }
    }
    return point == 0 ? 1 : 0;
}

